export const changDragPosition = (items, yPos, newYPos, currentOrder, heightOfElement, spaceBetweenRow = 0) => {
    let variance = newYPos - yPos;
    let axist = variance > 0 ? 1 : -1; // -1 is to top, 1 is to bottom

    variance = Math.abs(variance);
    if(axist + currentOrder < 0 || axist + currentOrder > items.length) return; // index out of range
    let newOrder = currentOrder;
    while(variance > heightOfElement) {
        newOrder += axist;
        variance -= (heightOfElement + spaceBetweenRow);
    }
    
    let tempItem = items.find((item, index) => index === currentOrder);
    items = items.filter((items));
    items.splice(newOrder, 0, tempItem);
    return items;
}
