export function changDragPosition(items, yPos, newYPos, currentOrder, heightOfElement, spaceBetweenRow = 0){
    console.log(items)
    let variance = newYPos - yPos;
    let axist = 1; // -1 is to top, 1 is to bottom
    let newOrder = currentOrder;
    if(variance < 0) {
        axist = -1;
    }
    // console.log(`${axist}: axist`) // ok
    variance = Math.abs(variance);

    console.log(`${newOrder}: new order`)
    if(axist + currentOrder < 0 || axist + currentOrder > items.length) return items; // index out of range

    while(variance > heightOfElement && !(newOrder + axist < 0) && !(newOrder + axist > items.length)) {
        newOrder += axist;
        variance -= (heightOfElement + spaceBetweenRow);
    }
    console.log(`${newOrder}: new order`)
    
    let tempItem = items.find((item, index) => index === currentOrder);
    let changeItem = items.find((item, index) => index === newOrder);
    console.log(tempItem)
    console.log(changeItem)

    (items[currentOrder]).title = changeItem[newOrder].title;
    (items[currentOrder]).description = changeItem[newOrder].description;
    items[newOrder].title = tempItem[currentOrder].title;
    items[newOrder].description = tempItem[currentOrder].description;

    return items;
}
