import { createRef, useState } from "react";
import { ReactComponent as SearchIcon } from "../../../assets/images/icon-search.svg";

import "./SearchField.scss";

export const SearchField = () => {
    const [isFocusing, setFocusing] = useState(false);
    const handleFocusing = (val) => {
        setFocusing(val);
    }
    const searchFieldRef = createRef();

    return (
        <div ref={searchFieldRef} className={`${isFocusing ? "ct__focusing" : ""} ct__search-field`}>
            <SearchIcon />
            <input type="text" placeholder='Search' onFocus={() => handleFocusing(true)} onClick={() => handleFocusing(false)}/>
        </div>
    );
}
